import { useState } from "react";
import { Container, Button,  Col, Row } from "react-bootstrap";

function Btn() {

    const [visible, setVisible] = useState(false);
    const [ isAdding, setIsAdding ] = useState(false);

    return(
        <Container fluid>
        <Row className="mt-3 text-center mb-4">
            <Col sm={{ span: 20 }}>
            <Button
                onClick={()=> {
                    setVisible(!visible)
                    setIsAdding(true);
                }}
            >
            + </Button>
            </Col>
            </Row>
        </Container>

    )}

export default Btn;
